export const getPaymentIcon = (payment_method = "") => {

  
  payment_method = capitaliseFirstLetter(payment_method)

  const [type, subType] = payment_method.split("/")

  if (paymentIcons[subType]) {
    return paymentIcons[subType]
  }

  if (paymentIcons[type]) {
    return paymentIcons[type]
  }


  return null
}
